mapper.select:
/* UrlInfoMapper.selectOriginUrl */
SELECT
    ORIGIN_URL AS originUrl
FROM URL_INFO
WHERE SHORT_URL = #{shortUrl}

mapper.update:
/* UrlInfoMapper.updateShortUrl */
UPDATE URL_INFO
SET
    SHORT_URL = #{shortUrl}
WHERE RID = #{rid}

/* UrlInfoMapper.updateUrl */
UPDATE URL_INFO
SET
    URL_CALL_NUM = #{urlCallNum}
WHERE RID = #{rid}

mapper.delete:
/* UrlInfoMapper.deleteUrl */
DELETE
FROM URL_INFO
WHERE RID = #{rid}
